mapper.select:
select

from transaction
where 1=1

  and device_sn = #{deviceSn}


  and  total_amount = #{totalAmount}


  and pay_amount = #{payAmount}


  and created_at = #{transDate}

select

from transaction
where 1=1

 and device_sn = #{deviceSn}


 and  total_amount = #{totalAmount}


  and pay_amount = #{payAmount}


  and agency_id = #{agencyId}


  and  DATE_FORMAT(created_at,'%Y-%m-%d')  = #{transDate}


  and ABS(TIME_TO_SEC(TIMEDIFF(#{transTime},DATE_FORMAT(created_at,'%H:%i:%s ')))) <= 60





  and card_number LIKE CONCAT('%',#{preCardNumber},'%')


  and card_number LIKE CONCAT('%',#{cardNumberEnd},'%')


  and status = #{status}


  and payment_type = #{paymentType}

order by created_at asc
limit 100

select

from transaction
where 1=1

  and device_sn = #{deviceSn}


  and  total_amount = #{totalAmount}


  and pay_amount = #{payAmount}


  and agency_id = #{agencyId}


  and  DATE_FORMAT(created_at,'%Y-%m-%d')  = #{transDate}


  and ABS(TIME_TO_SEC(TIMEDIFF(#{transTime},DATE_FORMAT(created_at,'%H:%i:%s ')))) <= 60


  and status = #{status}


  and payment_type = #{paymentType}

order by created_at asc
limit 100
